import React, { useState } from "react";
import "./App.css";
import TextEditor from "./components/TextEditor";

function App() {
    const [flag, setFlag] = useState(true);

    const handleClick = () => {
        setFlag((prevState) => {
            return !prevState;
        });
    };

    const editorTemplate = (
        <div className="editor">
            <TextEditor />
        </div>
    );

    const [notes, setNotes] = useState(null);
    // notes = fetch("http://localhost:8000/blogs")
    //     .then((res) => res.json())
    //     .then((res) => res)
    //     .catch(console.log);
    // console.log(notes);

    fetch("http://localhost:8000/blogs", {
        method: "GET",
        headers: {
            Accept: "application/json",
        },
    })
        .then((response) => response.json())
        .then((response) => setNotes(JSON.stringify(response)));

    console.log(Array.isArray(notes));

    // const notesCont =

    return (
        <div className="App">
            <header className="App-header">
                <img src="https://images.unsplash.com/photo-1676407591026-824246b4a102?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=1770&q=80" />
                <button onClick={handleClick}>Notes</button>
                <button onClick={handleClick}>Create Note</button>
            </header>
            {flag ? editorTemplate : <div></div>}
        </div>
    );
}

export default App;
