import React, { useState } from "react";
import "./App.css";
import TextEditor from "./components/TextEditor";
import { EditorState, convertToRaw, convertFromRaw } from "draft-js";

function App() {
    const [flag, setFlag] = useState(true);

    const handleClick = () => {
        setFlag((prevState) => {
            return !prevState;
        });
    };

    const editorTemplate = (
        <div className="editor">
            <TextEditor />
        </div>
    );

    const [notes, setNotes] = useState(null);
    async function getNotes() {
        const response = await fetch("http://localhost:8000/blogs");
        const obj = await response.json();
        setNotes(JSON.parse(obj));
    }
    getNotes();

    let oneNote = notes ? convertFromRaw(JSON.parse(notes)[0]) : null;
    console.log(oneNote);

    return (
        <div className="App">
            <header className="App-header">
                <img src="https://images.unsplash.com/photo-1676407591026-824246b4a102?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=1770&q=80" />
                <button onClick={handleClick}>Notes</button>
                <button onClick={handleClick}>Create Note</button>
            </header>
            {flag ? editorTemplate : <div></div>}
        </div>
    );
}

export default App;
