import React, { useState } from "react";
import "./App.css";
import TextEditor from "./components/TextEditor";
import { EditorState, convertToRaw, convertFromRaw } from "draft-js";
import draftToHtml from "draftjs-to-html";
import Card from "./components/Card";
import { Editor } from "react-draft-wysiwyg";

function App() {
    const [flag, setFlag] = useState(true);

    const handleClick = () => {
        setFlag((prevState) => {
            return !prevState;
        });
    };

    const editorTemplate = (
        <div className="editor">
            <TextEditor />
        </div>
    );

    const [notes, setNotes] = useState(null);
    async function getNotes() {
        const response = await fetch("http://localhost:8000/blogs");
        const obj = await response.json();
        setNotes(JSON.stringify(obj));
        // console.log(notes);
    }
    getNotes();
    // draftToHtml(JSON.parse(outputData))
    // JSON.parse(outputData);
    let noteTemplate = [],
        size = 0;
    if (notes) {
        size = JSON.parse(notes).length;
        console.log(size);
    }
    for (let index = 0; index < size; index++) {
        let oneNote = notes ? convertFromRaw(JSON.parse(notes)[index]) : null;
        console.log(oneNote);
        noteTemplate.push(oneNote);
    }

    return (
        <div className="App">
            <header className="App-header">
                <img src="https://images.unsplash.com/photo-1676407591026-824246b4a102?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=1770&q=80" />
                <button onClick={handleClick}>Notes</button>
                <button onClick={handleClick}>Create Note</button>
            </header>
            <Card description="Designers react to JavaScript. Join us in our monthly meetup where designers gather to share newbie React knowledge." />
            {flag ? (
                editorTemplate
            ) : (
                <div>
                    {noteTemplate.map((item) => {
                        return (
                            <Editor
                                editorState={EditorState.createWithContent(
                                    item
                                )}
                                // onChange={this.onChange}
                            />
                        );
                    })}
                </div>
            )}
        </div>
    );
}

export default App;
